import React, { useState, useEffect, useRef } from "react";
import "./app.css";

type Task = { id: number; text: string };
type Note = { id: number; text: string };

const initialTasks: Task[] = [
  { id: 1, text: "Task 1" },
  { id: 2, text: "Task 2" },
  { id: 3, text: "Task 3" },
  { id: 4, text: "Task 4" },
];

const suggestedTasks: Task[] = [
  { id: 5, text: "Task 1" },
  { id: 6, text: "Task 2" },
];

const notes: Note[] = [
  { id: 1, text: "Note #1" },
  { id: 2, text: "Recording #1" },
];

export default function App() {
  const [tasks, setTasks] = useState<Task[]>(initialTasks);
  const [isListening, setIsListening] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [status, setStatus] = useState("Listening for 'Hey Google'...");

  const recognitionRef = useRef<any>(null);
  const silenceTimerRef = useRef<number | null>(null);
  const taskIdRef = useRef(7);

  useEffect(() => {
    // Check if speech recognition is supported
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setStatus("Speech recognition not supported in this browser");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onresult = (event: any) => {
      const lastResult = event.results[event.results.length - 1];
      const transcript = lastResult[0].transcript.toLowerCase().trim();

      if (!isRecording) {
        // Listen for wake phrase
        if (transcript.includes('hey google')) {
          setIsRecording(true);
          setStatus("üî¥ Recording... (will stop after 3 seconds of silence)");

          // Clear any existing timer
          if (silenceTimerRef.current) {
            window.clearTimeout(silenceTimerRef.current);
          }
        }
      } else {
        // Recording mode - capture the task
        const taskText = transcript.replace(/hey google/gi, '').trim();

        if (taskText && lastResult.isFinal) {
          // Add the task
          const newTask: Task = {
            id: taskIdRef.current++,
            text: taskText
          };

          setTasks(prev => [...prev, newTask]);
          setIsRecording(false);
          setStatus("‚úÖ Task added! Listening for 'Hey Google'...");

          // Clear silence timer
          if (silenceTimerRef.current) {
            window.clearTimeout(silenceTimerRef.current);
          }
        }

        // Reset silence timer
        if (silenceTimerRef.current) {
          window.clearTimeout(silenceTimerRef.current);
        }

        silenceTimerRef.current = window.setTimeout(() => {
          if (isRecording) {
            setIsRecording(false);
            setStatus("‚è∞ Stopped recording (silence timeout). Listening for 'Hey Google'...");
          }
        }, 3000);
      }
    };

    recognition.onerror = (event: any) => {
      console.error('Speech recognition error:', event.error);
      setStatus(`Error: ${event.error}. Listening for 'Hey Google'...`);
    };

    recognition.onend = () => {
      // Restart recognition to keep it continuous
      if (recognitionRef.current) {
        setTimeout(() => {
          try {
            recognitionRef.current?.start();
          } catch (error) {
            console.log('Recognition restart prevented:', error);
          }
        }, 100);
      }
    };

    recognitionRef.current = recognition;

    // Start listening
    try {
      recognition.start();
    } catch (error) {
      setStatus("Error starting speech recognition");
    }

    // Cleanup
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (silenceTimerRef.current) {
        window.clearTimeout(silenceTimerRef.current);
      }
    };
  }, [isRecording]);

  return (
    <div className="container">
      {/* Speech Status */}
      <div className="speech-status">
        <div className={`status-indicator ${isListening ? 'listening' : ''} ${isRecording ? 'recording' : ''}`}>
          {isRecording ? 'üî¥' : isListening ? 'üé§' : '‚ùå'}
        </div>
        <span>{status}</span>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Left - Tasks */}
        <div className="sidebar">
          <h3>üìã Tasks ‚ûï</h3>
          <ul className="list">
            {tasks.map((task) => (
              <li key={task.id} className="list-item">
                {task.text}
              </li>
            ))}
          </ul>

          <h4>Suggested Tasks</h4>
          <ul className="list">
            {suggestedTasks.map((task) => (
              <li key={task.id} className="list-item">
                {task.text}
              </li>
            ))}
          </ul>
        </div>

        {/* Middle - Home Button */}
        <div className="center">
          <button className="home-button">Home</button>
        </div>

        {/* Right - Notes */}
        <div className="sidebar">
          <h3>üìù Notes ‚¨á</h3>
          <ul className="list">
            {notes.map((note) => (
              <li key={note.id} className="list-item">
                {note.text}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
